<body>
    <table style="width:100%; border-collapse: collapse; font-size:11px;" cellspacing="0" cellpadding="0">
    <caption style="width:100%; border-collapse: collapse; font-size:12px;"><center><b>PURCHASE ORDER</b></center></caption>  
    <tr style="line-height:8px;">
        <td style="width:35%; border: 0; border-bottom: 1px solid black;"><b>PO Number:{{doc.name}}</b></td> 
        <td style="width:40%; border: 0; border-bottom: 1px solid black;" colspan = 2 ><b><center>Dated:{{doc.transaction_date}}</center></b></td> 
        <td style="width:24%; border: 0; border-bottom: 1px solid black;" colspan = 2><b></b></td> 
    </tr>
    <tr>
        <td style="border: 0; border-bottom: 1px solid black;" rowspan=5>
            <b>TO</b>
            <br>
            <b>{{doc.supplier}}</b>
            <br>
            {% set val1 = doc.supplier_address %}
            {%if val1 != None%}
              {{frappe.db.get_value("Address",val1,"address_line1")}}<br>
              {% if frappe.db.get_value("Address",val1,"address_line2") == None %}
                {{ " "}}
              {% else %}
                {{frappe.db.get_value("Address",val1,"address_line2")}}
              {% endif %}
              {{frappe.db.get_value("Address",val1,"city")}}


              <br>
              {% if frappe.db.get_value("Address",val1,"gst_state") == None %}
                {{ " "}}
              {% else %}
                {{frappe.db.get_value("Address",val1,"gst_state")}}-{{frappe.db.get_value("Address",val1,"pincode")}}.
              {% endif %}

              <br>Phone: 
              {% if frappe.db.get_value("Address",val1,"Phone") == None %}
                {{ " "}}
              {% else %}
                +91-{{frappe.db.get_value("Address",val1,"Phone")}}
              {% endif %}

              <br>
              Fax:
              {% if frappe.db.get_value("Address",val1,"fax") == None %}
                {{ " "}}
              {% else %}
                {{frappe.db.get_value("Address",val1,"fax")}} 
              {% endif %}

            {% endif %}
        </td>
        <td style="border: 0; border-bottom: 0px solid black;line-height: 9px;">
            <b>Type of Supplier</b>
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                : {{frappe.db.get_value("Supplier",{"name":doc.supplier},"supplier_group")}}
            </b>
        </td>

        <td style="border: 0; border-bottom: 0px solid black;line-height: 9px;">
            <!--<b>Vendor Code</b>-->
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <!--<b>-->
            <!--    :{{doc.supplier}}-->
            <!--</b>-->
        </td>

    </tr>
    <tr>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>GST Vend Class</b>
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                :{% if doc.gst_category != None %} {{doc.gst_category}} {%endif%}
            </b>
        </td>

        <td style="border: 0; border-bottom: 0px solid black;">
            <!--<b>Your Ref No.</b>-->
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                <!--:{%if doc.custom_customer_purchase_order%}{{doc.custom_customer_purchase_order}}{%endif%}-->
            </b>
        </td>

    </tr>
    <tr>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>Vendor Gstin</b>
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                : {%if doc.supplier_gstin != None%}{{doc.supplier_gstin}}{%endif%}
            </b>
        </td>

        <td style="border: 0; border-bottom: 0px solid black;">
            <!--<b>Your Ref Date.</b>-->
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                <!--: {%if doc.custom_customer_purchase_order_date%}{{doc.custom_customer_purchase_order_date}}{%endif%}-->
            </b>
        </td>

    </tr>
    <tr>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>Type of Tax</b>
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                :{%if frappe.db.get_value("Address",val1,"state") == frappe.db.get_value("Address",doc.supplier_address,"state") %}
                    {{"CGST&SGST"}}
                {%else%}
                    {{"IGST"}}
                {%endif%}
            </b>
        </td>

        <td style="border: 0; border-bottom: 0px solid black;">
            <!--<b>Plant Code</b>-->
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                <!--:{%if doc.custom_plant_code%}{{doc.custom_plant_code}}{%endif%}-->
            </b>
        </td>

    </tr>
     <tr>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>Vendor Pan</b>
        </td>
        <td style="border: 0; border-bottom: 0px solid black;">
            <b>
                :{% if frappe.db.get_value("Supplier",{"name":doc.supplier},"pan") != None%} {{frappe.db.get_value("Supplier",{"name":doc.supplier},"pan")}}{%endif%}
