    <body>
        Sir/Madam,<br>
        <p style="text-indent: 20px;">
            We have pleasure to entrust you with our order for the goods mentioned below as per the conditions.
            Kindly send us back the duplicate copy of this order duly acknowledged.
        </p>


    {%set tax_app = None %}
    {%if frappe.db.get_value("Address",val1,"state") == frappe.db.get_value("Address",doc.supplier_address,"state") %}
                    {%set tax_app = "CGST&SGST" %}
                {%else%}
                    {%set tax_app = "IGST" %}
                {%endif%}

    <table style="line-height: 1em;width: 100%; font-size:11px" border="2" cellpadding="0" cellspacing="0">
        <thead style="background-color:white">
        <tr>
            <th style="text-align:center;font-size:9px;" width= 4%><b>Sl.</b></th>
            <th style="text-align:center;font-size:9px;" width= 10%><b>Part No.</b></th>
            <th style="text-align:center;font-size:9px;" width= 15%><b>Description</b></th>
            <th style="text-align:center;font-size:9px;" width= 6%><b>HSN/SAC Code</b></th>
            <th style="text-align:center;font-size:9px;" width= 6%><b>Qty</b></th>
            <th style="text-align:center;font-size:9px;" width= 7%><b>Unit Price (Rs)</b></th>
            <th style="text-align:center;font-size:9px;" width= 6%><b>Disc</b></th>
            <th style="text-align:center;font-size:9px;" width= 10%><b>Taxable Amount</b></th>
            {% if tax_app == "CGST&SGST" %} 
            <th style="text-align:center;font-size:9px;" width= 4%><b>CGST %</b></th>
            <th style="text-align:center;font-size:9px;" width= 6%><b>CGST Amount</b></th>
            <th style="text-align:center;font-size:9px;" width= 4%><b>SGST %</b></th>
            <th style="text-align:center;font-size:9px;" width= 6%><b>SGST Amount</b></th>
            {%else%}
            <th style="text-align:center;font-size:9px;" width= 8%><b>IGST %</b></th>
            <th style="text-align:center;font-size:9px;" width= 12%><b>IGST Amount</b></th>
            {%endif%}
            <th style="text-align:center;font-size:9px;" width= 12%><b>Amount (Rs)</b></th>
        </tr>
        </thead>
        <tbody style="font-size:10px;">
        {% for item in doc.items %}
        <tr>
            <td>{{item.idx}}</td>
            <td>{{item.item_code}}</td>
            <td>{{item.description}}</td>
            <td>{{item.gst_hsn_code}}</td>
            <td>{{item.qty}}</td>
            <td>{{item.rate}}</td>
            <td>{{item.discount_amount}}</td>
            <td>{{item.net_amount}}</td>
            {% if tax_app == "CGST&SGST" %} 

                <td>
                    {%set tax = [0] %}
                            {% for row in doc.taxes %}
                {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
                {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                    {%if item_code==item.item_code%}
                    {% if row.rate== 0 %}

                    {% if tax_amounts[0] > 0 %}
    		{% set _ =tax.append( tax.pop() + tax_amounts[0]) %}

    		{%endif%} {% else %}
            {% set _ =tax.append( tax.pop() + row.rate) %}  {#{row.rate}#}
                    {%endif%}{%endif%}
                    {% endfor %}
                {% endfor %} {% if tax[0] != 0 %}{{tax[0]/2}} % {%else%} {{tax[0]}} {%endif%}
                </td>
                <td style="text-align:right;">
                    {#%if item.item_tax_template != None%#}
    		{%set taxrate = [0] %}
                            {% for row in doc.taxes %}
                {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
                {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                    {%if item_code==item.item_code%}
                    {#% if row.rate== 0 %#}

                    {#% if tax_amounts[0] > 0 %#}
    		{% set _ =taxrate.append( taxrate.pop() + tax_amounts[1]) %}

    		{%endif%}
                    {% endfor %}
                {% endfor %} {%if taxrate[0] != 0 %} {{taxrate[0]/2 |abs |round(2, 'ceil')}} {%else%}{{taxrate[0] |abs |round(2, 'ceil')}} {%endif%}
                </td>

                <td>
                    {%set tax = [0] %}
                            {% for row in doc.taxes %}
                {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
                {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                    {%if item_code==item.item_code%}
                    {% if row.rate== 0 %}

                    {% if tax_amounts[0] > 0 %}
    		{% set _ =tax.append( tax.pop() + tax_amounts[0]) %}

    		{%endif%} {% else %}
            {% set _ =tax.append( tax.pop() + row.rate) %}  {#{row.rate}#}
                    {%endif%}{%endif%}
                    {% endfor %}
                {% endfor %} {% if tax[0] != 0 %}{{tax[0]/2}} % {%else%} {{tax[0]}} {%endif%}

                </td>
                <td style="text-align:right;">
                    {#%if item.item_tax_template != None%#}
    		{%set taxrate = [0] %}
                            {% for row in doc.taxes %}
                {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
                {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                    {%if item_code==item.item_code%}
                    {#% if row.rate== 0 %#}

                    {#% if tax_amounts[0] > 0 %#}
    		{% set _ =taxrate.append( taxrate.pop() + tax_amounts[1]) %}

    		{%endif%}
                    {% endfor %}
                {% endfor %} 
                {%if taxrate[0] != 0 %} {{taxrate[0]/2 |abs |round(2, 'ceil')}} {%else%}{{taxrate[0] |abs |round(2, 'ceil')}} {%endif%}
                </td>

            {%else%}
                <td>
                    {%set tax = [0] %}
                            {% for row in doc.taxes %}
                {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
                {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                    {%if item_code==item.item_code%}
                    {% if row.rate== 0 %}

                    {% if tax_amounts[0] > 0 %}
    		{% set _ =tax.append( tax.pop() + tax_amounts[0]) %}

    		{%endif%} {% else %}
            {% set _ =tax.append( tax.pop() + row.rate) %}  {#{row.rate}#}
                    {%endif%}{%endif%}
                    {% endfor %}
                {% endfor %} {{tax[0]}} %

                </td>
                <td style="text-align:right;">
                    {#%if item.item_tax_template != None%#}
    		{%set taxrate = [0] %}
                            {% for row in doc.taxes %}
                {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
                {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                    {%if item_code==item.item_code%}
                    {#% if row.rate== 0 %#}

                    {#% if tax_amounts[0] > 0 %#}
    		{% set _ =taxrate.append( taxrate.pop() + tax_amounts[1]) %}

    		{%endif%}
                    {% endfor %}
                {% endfor %} {{taxrate[0] |abs |round(2, 'ceil')}} {#%endif%#}
                </td>
            {%endif%}
            <td style="text-align:right;">{{item.amount}}</td>
        </tr>
        {%endfor%}
        </tbody>

        <tr>
            <td width = 50% rowspan = 2 colspan = 5>
                Payment Terms:<br>
                {%if doc.payment_terms_template%}
                {{doc.payment_terms_template}}
                {%endif%}

                {%if doc.payment_schedule%}
                {% if doc.payment_schedule[0].payment_term != None%}    {{doc.payment_schedule[0].payment_term}} {%endif%}
                {%endif%}
                <br>
                Note:<br>
                {%if doc.terms%}
                    {{doc.terms}}
                {%endif%}
            </td>
            {%set colspan = 6 %}
            {%if tax_app == "CGST&SGST" %}
             {%set colspan = 8%}
            {%endif%}
            <td width = 50% colspan = {{colspan}}><b>
                TCS @ {% for tax in doc.taxes%}
                {% if "TCS" in tax.account_head %}
                {{tax.rate}}% : {{tax.tax_amount}} <br> {%endif%}
                {%endfor%}<br>
                Grand Total : {{doc.grand_total}}<br>
                In Words : {{doc.in_words}}
            </b></td>
        </tr>
        <tr>
            <td colspan = {{colspan}}>
                Destination:<br>
                Our GSTIN : {%if doc.company_gstin != None%}{{doc.company_gstin}}{%endif%}<br>
                Our PAN : {%if frappe.db.get_value("Company",{"name":doc.company},"pan") != None%} {{frappe.db.get_value("Company",{"name":doc.company},"pan")}}{%endif%}<br>
                Our CIN : {%if frappe.db.get_value("Company",{"name":doc.company},"tax_id") != None%} {{frappe.db.get_value("Company",{"name":doc.company},"tax_id")}} {%endif%}<br>
                Contact Person : <br>
                Contact Number : <br>
            </td>
        </tr>
        <tr>
            <td colspan = {{colspan}} style= "border-right:0">
                *All other terms and conditions as per our Standard Format Enclosed.<br>
                *The components/spare parts for our final products, covered under this P.O. may also be used for after sales support activities/sales.<br>
                *This is a digitally generated PO.
            </td>
            <td colspan = 5 style = "border-left:0">
                <b>For {{doc.company}}</b> 
            </td>
        </tr>
    </table>

    </body>
</html>"}]
